import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [credentials, setCredentials] = useState({ email: '', password: '', role: 'user' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(credentials)
            });

            const data = await res.json();
            if (res.ok) {
                localStorage.setItem('token', data.token);
                localStorage.setItem('role', credentials.role);
                if (credentials.role === 'admin') {
                    navigate('/admin');
                } else {
                    navigate('/user');
                }
            } else {
                alert(data.message || 'Login failed');
            }
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-md mx-auto mt-10 p-6 shadow bg-white rounded">
            <h2 className="text-xl font-bold mb-4">Login</h2>
            <input type="email" name="email" placeholder="Email" onChange={handleChange} required className="w-full p-2 border mb-2" />
            <input type="password" name="password" placeholder="Password" onChange={handleChange} required className="w-full p-2 border mb-2" />
            <select name="role" onChange={handleChange} className="w-full p-2 border mb-4">
                <option value="user">User</option>
                <option value="admin">Admin</option>
            </select>
            <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">Login</button>
        </form>
    );
};

export default Login;
