// TenantList component 
import React, { useEffect, useState } from 'react';

const TenantList = () => {
    const [tenants, setTenants] = useState([]);

    const fetchTenants = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/tenants');
            const data = await response.json();
            setTenants(data);
        } catch (error) {
            console.error('Error fetching tenants:', error);
        }
    };

    const markAsPaid = async (id) => {
        const utr = prompt('Enter UTR number or type "cash" if paid by cash:');
        if (!utr) return;

        try {
            const response = await fetch(`http://localhost:5000/api/tenants/${id}/pay`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ paymentStatus: 'paid', utrNumber: utr }),
            });

            if (response.ok) {
                fetchTenants(); // Refresh list
            } else {
                alert('Failed to update payment status');
            }
        } catch (error) {
            console.error('Error marking as paid:', error);
        }
    };

    useEffect(() => {
        fetchTenants();
    }, []);

    return (
        <div className="bg-white p-4 rounded shadow max-w-4xl">
            <h2 className="text-lg font-semibold mb-2">Tenants</h2>
            {tenants.length === 0 ? (
                <p>No tenants found.</p>
            ) : (
                <table className="w-full border text-sm">
                    <thead>
                        <tr className="bg-gray-100 text-left">
                            <th className="p-2">Name</th>
                            <th className="p-2">House Address</th>
                            <th className="p-2">Phone</th>
                            <th className="p-2">Status</th>
                            <th className="p-2">UTR</th>
                            <th className="p-2">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tenants.map((tenant) => (
                            <tr key={tenant._id} className="border-t">
                                <td className="p-2">{tenant.name}</td>
                                <td className="p-2">{tenant.houseAddress}</td>
                                <td className="p-2">{tenant.phone}</td>
                                <td className="p-2">
                                    {tenant.paymentStatus === 'paid' ? '✅ Paid' : '❌ Pending'}
                                </td>
                                <td className="p-2">{tenant.utrNumber || '-'}</td>
                                <td className="p-2">
                                    {tenant.paymentStatus !== 'paid' && (
                                        <button
                                            className="bg-green-600 text-white px-2 py-1 rounded hover:bg-green-700"
                                            onClick={() => markAsPaid(tenant._id)}
                                        >
                                            Mark as Paid
                                        </button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default TenantList;
